import React, { useState, useEffect } from 'react';
import check from '../Axios/LoginCheckAxios';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import font from '../styles/font.css';

function LoginCheck() {

    const [userName, setUserName] = useState('');

    function callback(data){
      
     setUserName(data);
    }

    useEffect(
        () => {
              check(callback);
        }, []
    );
          return(
              <>
           
              
          <NavItem>{userName}<Welcome>님 환영합니다</Welcome></NavItem>
        <NavItem><Link to="" style={{ textDecoration: 'none',color:'balck'  ,fontFamily: 'Jua, sanserif', marginLeft:'20px'}}>Logout</Link></NavItem>
                
    
            </>
          );

                         
  
}

export default LoginCheck;

const NavItem = styled.li`
    width: auto;
    margin-top:5px;
    display: flex;
    color: black;
    float:right;
    position:relative;
    font-family:'Jua', sans-serif;
`

const Welcome = styled.span`
    font-size: 0.6rem;
    
`
const User = styled.span`
   font-size: 0.8rem;
   font-weight: bold;
   color: blueviolet;
`
