import { Component, Fragment } from 'react';
import customAxios from './customAxios';

class App extends Component {
  render(){
  const [password, setPassword] = useState("")
  const [disabled, setDisabled] = useState(false)

  const handleChange = ({ target: { value } }) => setPassword(value)



  const handleSubmit = async (event) => {
    setDisabled(true)
    event.preventDefault()
    await new Promise((r) => setTimeout(r, 1000))
    alert(`변경된 패스워드: ${password}`)

    setDisabled(false)
  }



  return (
    <Fragment>
      <div className="login">
      <form onSubmit={handleSubmit}>
        <div className="id">
      <input type="text" name="id" placeholder="아이디" onChange={handleChange} ></input>
      </div>
      <div className="pwd"> 
        <input type="text" name="password" 
        onChange={handleChange} placeholder="비밀번호"></input>
        </div>
        <div className="age">
        <input type="text" name="age"  placeholder="나이"></input>
        </div>
        <div className="address">
        <input type="text" name="address"  placeholder="주소"></input>
        </div>
        <div className="loginBtn">
      <button type="submit" disabled={disabled}>입력</button>
      </div>
      </form>
      </div>
    </Fragment>


  );
  }
}

export default App;
