import React, { useState, useEffect } from 'react';
import getBoard from '../Axios/BoardListAxios';
import { Link } from 'react-router-dom';

function BoardList() {

    const [board, setTitle] = useState([]);
    

    function callback(data){
        setTitle(data);
        
    }

    useEffect(
        () => {
            getBoard(callback);
        }, []
    );
    const boardTitle = board.map((board,index ) =><div key={index}><Link to='/boardDetail' style={{ textDecoration: 'none',color:'#ea2c62' }}>{board.title}</Link><br/></div>);
  
    return(
   <div>
    <div>{boardTitle}</div>
   </div>
    );
}

export default BoardList;
