import React, { useState, useEffect } from 'react';
import comt from '../Axios/CommentAxios';
import styled from 'styled-components';

function CommentList() {

    const [comment, setTitle] = useState([]);
    const [commentNum, setCommentNum] = useState('');

    function callback(data){
        setTitle(data);
        
    }

    function callbackk(data){
        setCommentNum(data);
    }

    useEffect(
        () => {
            comt(callback, callbackk);
        }, []
    );
    const Comments = comment.map((comment,index ) =><Project key={index}>
    <Pimage><UserName>{comment.userName}</UserName>{comment.conts}</Pimage><br></br><br></br></Project>);

    return(
        <div>
      <CommentCount>
      <h6>[ 방명록 {commentNum} 개 ]</h6> 
    </CommentCount> 
   <Line></Line>
       <CommentConts>
       {Comments}
           </CommentConts>   
     
        </div>

    );
}

export default CommentList;


const Project = styled.div `
   text-align:center;
`

const Pimage = styled.div `
    
   

`
const UserName = styled.span`
    margin-right:50px;
    font-weight:bold;
`
const CommentCount = styled.div`
    margin-right:360px;
   margin-top:30px;
`
const CommentConts = styled.div `
   margin-top:50px;
`
const Conts = styled.h5`
  color:black;
  
`
const Line = styled.hr`
   width:26rem;
`
