import React, { useState, useEffect } from 'react';
import check from '../Axios/LoginCheckAxios';
import { Link } from 'react-router-dom';
import styled from 'styled-components';

function LoginCheck() {

    const [userName, setUserName] = useState('');

    function callback(data){
     setUserName(data);
    }

    useEffect(
        () => {
              check(callback);
        }, []
    )
        return (
            <>
               {
                (function(){
                    if({userName} === '') return (<><NavItem><User>{userName}</User> <Welcome>&nbsp;님 환영합니다</Welcome></NavItem>
                        <NavItem><Link style={{ textDecoration: 'none',color:'balck'  ,fontFamily: 'Jua, sanserif', marginLeft:'20px'}}>Logout</Link></NavItem></>);
                    else return (<> <NavItem><Link to='/login' style={{ textDecoration: 'none' ,color:'black'  ,fontFamily: ' sanserif'}}>Login</Link></NavItem> 
                    <NavItem><Link to='/join' style={{ textDecoration: 'none' ,color:'black'  ,fontFamily: 'sanserif'}}>Join</Link></NavItem></>);
               
                })()
                   
                }
           
            </>
        );
  
}

export default LoginCheck;

const NavItem = styled.li`
    width: auto;
    margin-top:5px;
    display: flex;
    color: black;
    float:right;
    position:relative;
    font-family:'Jua', sans-serif;
`

const Welcome = styled.span`
    font-size: 0.6rem;
    
`
const User = styled.span`
   font-size: 0.8rem;
   font-weight: bold;
   color: blueviolet;
`
