import React, { Component, useEffect } from 'react';
import styled from  'styled-components';
import Iframe from 'react-iframe'
import { Link, DirectLink, Element, Events, animateScroll as scroll, scrollSpy, scroller } from 'react-scroll'
 

class PaperHouse extends Component{
    constructor(props) {
        super(props);
        this.scrollToTop = this.scrollToTop.bind(this);
      }
      scrollToTop() {
        scroll.scrollToTop();
      }

      scrollToDown(){
          scroll.scrollTo(100,500);
      }
     
      componentDidMount(){
        scroll.scrollToTop();
      }
    
    render() {
        
        return(
                 <PaperHouseForm >
                     <PaperHouseTitle>PaperHouse</PaperHouseTitle>
                     <VideoForm>
                     <video controls autoPlay>
                         <source src={process.env.PUBLIC_URL+'/video/PaperHouse.mp4'} type="video/mp4"></source>
                     </video>
                     </VideoForm>
                     <GoTop><Top onClick={this.scrollToTop}>시연영상</Top><br></br><br></br><Top onClick={this.scrollToDown}>개발기술서</Top></GoTop>
                   <Pdf>
                       <Title>개발기술서</Title>
                   <Iframe marginWidth='0' width="45%" height="120%" scrolling='no' frameBorder='0' src={process.env.PUBLIC_URL+'/img/PaperHouse.pdf'} style={{marginLeft:'20%'}}/>
                   </Pdf>
                  
                 </PaperHouseForm>
        );
    }
}

export default PaperHouse;

const PaperHouseForm = styled.div `
   background-color:#e4e978;
   width:100%;
   height:246%;
   text-align:center;
   display:flow-root;
   
`

const PaperHouseTitle = styled.h2`
    color: #d37815;
    
`
const VideoForm = styled.div `
  
`

const GoTop = styled.div `
  
  position: fixed;
    width: 92px;
    height: 24.5px;
    left: 1380px;
    top: 250px;
    margin:0 auto;
`
const Top = styled.a`
  color:#34626c;
  
    font-size: 1.15em;
`

const Pdf = styled.div `
 
  margin:0 auto; 
  width:80%;
  height:37.1%;
  background-color:#e4e978;
  text-align:center;
`

const Title = styled.h2 `
 margin-right:50%;
`
