import React, { useState, useEffect } from 'react';
import comt from '../Axios/CommentAxios';
import styled from 'styled-components';

function CommentList() {

    const [comment, setTitle] = useState([]);
    const [commentNum, setCommentNum] = useState('');

    function callback(data){
        setTitle(data);
        
    }

    function callbackk(data){
        setCommentNum(data);
    }

    useEffect(
        () => {
            comt(callback, callbackk);
        }, []
    );
    const Comments = comment.map((comment,index ) =><Project key={index}><br></br><br></br><br></br><br></br>
    <Pimage><Conts>{comment.conts}</Conts></Pimage><br></br><br></br></Project>);

    return(
        <div>
      <div>{commentNum}</div>      
       {Comments}
        </div>

    );
}

export default CommentList;


const Project = styled.div `
   text-align:center;
`

const Pimage = styled.div `
    
   

`

const Conts = styled.h5`
  color:black;
  
`
