import React, { Component } from 'react';
import Header from '../Layout/Header';
import styled from 'styled-components';


const API_KEY = '341bccdb1aa9d2c8eb70aac75a9cabbd';


class Home extends Component {

    constructor(props) {
      super(props)
      this.state = {
        temperature: 0,
        name: '',
        icon: '',
      }
    }
  
    getWeather = () => {
      fetch(`http://api.openweathermap.org/data/2.5/weather?q=Seoul&APPID=${API_KEY}`)
      .then(response => response.json())
      .then(json => {
        this.setState({
          temperature: Math.floor(json.main.temp - 273.15),
          name: json.weather[0].main,
          icon: json.weather[0].icon,
        });
      });
    }
  
    componentDidMount() {
      this.getWeather()
    }
  
    render() {
        const { temperature, name, icon } = this.state;
        const img_url = `http://openweathermap.org/img/w/${icon}.png`;
  
        return (
          <HomeForm>         
         
          
          </HomeForm>
        );
      }
    }
    
  export default Home;


  const HomeForm = styled.div`
    text-align:center;
     height:100vh;
     width:100vw;
     background-color: aliceblue;
     background-image:url(${process.env.PUBLIC_URL+ '/img/Home.jpg'});
     background-size:90%;
