import React, { useState, useEffect } from 'react';
import getBoard from '../Axios/BoardListAxios';
import { Link } from 'react-router-dom';
import styled from 'styled-components';

function BoardList() {

    const [board, setTitle] = useState([]);
    

    function callback(data){
        setTitle(data);
        
    }

    useEffect(
        () => {
            getBoard(callback);
        }, []
    );

    
    const boardTitle = board.map((board,index ) =><Project key={index}><Link to='/boardDetail' style={{ textDecoration: 'none',color:'#ea2c62', fontSize:'40px' }}>{board.title}</Link>
    <Pimage><img height='auto' width='80%' src={process.env.PUBLIC_URL +'/img/'+ board.image} alt={index}/>
    <h6>{board.conts}</h6></Pimage><br></br><br></br></Project>);
  
    return(
   <div>
    <div>{boardTitle}</div>
   </div>
    );
}

export default BoardList;

const Project = styled.div `
   text-align:center;
`

