import React, {Component} from 'react';
import styled from  'styled-components';
import idCheck from '../Axios/JoinAxios';

class Join extends Component {
    state = {
        id : ''
    }

    idChange = (e) => {
        this.setState({
            id:e.target.value
        });
    }

    idCheck = () => {
        alert(this.state.id);
        idCheck(this.state.id);
    }
    render() {
        return(
            <JoinForm>
                <JoinText>회원가입</JoinText>
                <div>
                    <input type='text' name='id' placeholder='아이디' onChange={this.idChange} value={this.state.id}></input>
                  <IdCheck type='button' className='idCheck' onClick={this.idCheck}>중복확인</IdCheck>  
                </div>
                <div>
                    <input type='text' className='password' placeholder='비밀번호'></input>
                </div>
                <div>
                    <input type='text' className='email' placeholder='이메일'></input>
                </div>
                <JoinBtn>
                    <button type='button' className='joinbtn' >회원가입</button>
                </JoinBtn>
            </JoinForm>
        )
    }
}

export default Join;

const JoinForm = styled.div`
   margin-top:180px;
` 

const JoinText = styled.h1`
    margin-left:20PX;
`

const JoinBtn= styled.div`
    margin-top:30px;
    margin-left:50px;
`
const IdCheck = styled.button`
    margin-left:10px;
`
